
public class Sample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
				// 삼항 연산자, 조건 연산자
				/*
				 * 조건 ? 식1: 식2;
				 * 조건 true == 식1 실행
				 * 조건 false == 식2 실행 
				 */
				
				// ctrl + alt + 화살표 아래 : 라인 복제
				// ctrl + d : 한줄 삭제
				int rslt = 0;
				rslt = 5 > 0 ? 10 : -10;
				System.out.println(rslt); // 10
				
				rslt = 5 < 0 ? 10 : -10;
				System.out.println(rslt); // -10
				
	}

}
